function Bidders_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Bidders');
	if(!$arrPerm['insert']) return false;

	$data = [
		'ContactID' => Request::lookup('ContactID', ''),
		'BidNo' => Request::val('BidNo', ''),
		'BidderType' => Request::val('BidderType', 'Bidder'),
		'CheckedIn' => Request::val('CheckedIn', ''),
		'QuickPay' => Request::val('QuickPay', ''),
		'TablePreference' => Request::val('TablePreference', ''),
		'Card' => Request::val('Card', ''),
	];


	// automatic MailingName if passed as filterer
	if(Request::val('filterer_MailingName')) {
		$data['MailingName'] = Request::val('filterer_MailingName');
	}

	// automatic Business if passed as filterer
	if(Request::val('filterer_Business')) {
		$data['Business'] = Request::val('filterer_Business');
	}

	// automatic Address1 if passed as filterer
	if(Request::val('filterer_Address1')) {
		$data['Address1'] = Request::val('filterer_Address1');
	}

	// automatic Address2 if passed as filterer
	if(Request::val('filterer_Address2')) {
		$data['Address2'] = Request::val('filterer_Address2');
	}

	// automatic City if passed as filterer
	if(Request::val('filterer_City')) {
		$data['City'] = Request::val('filterer_City');
	}

	// automatic State if passed as filterer
	if(Request::val('filterer_State')) {
		$data['State'] = Request::val('filterer_State');
	}

	// automatic Zip if passed as filterer
	if(Request::val('filterer_Zip')) {
		$data['Zip'] = Request::val('filterer_Zip');
	}

	// hook: Bidders_before_insert
	if(function_exists('Bidders_before_insert')) {
		$args = [];
		if(!Bidders_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Bidders', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Bidders', $recID, calculated_fields()['Bidders']);

	// hook: Bidders_after_insert
	if(function_exists('Bidders_after_insert')) {
		$res = sql("SELECT * FROM `Bidders` WHERE `ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Bidders_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Bidders', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Bidders_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function Bidders_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

