function Bidders_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('Bidders', $selected_id, 'edit')) return false;

	$data = [
		'ContactID' => Request::lookup('ContactID', ''),
		'BidNo' => Request::val('BidNo', ''),
		'BidderType' => Request::val('BidderType', ''),
		'CheckedIn' => Request::val('CheckedIn', ''),
		'QuickPay' => Request::val('QuickPay', ''),
		'TablePreference' => Request::val('TablePreference', ''),
		'Card' => Request::val('Card', ''),
	];

	// get existing values
	$old_data = getRecord('Bidders', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Bidders_before_update
	if(function_exists('Bidders_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Bidders_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Bidders', 
		backtick_keys_once($set), 
		['`ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Bidders_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Bidders', $data['selectedID'], calculated_fields()['Bidders']);

	// hook: Bidders_after_update
	if(function_exists('Bidders_after_update')) {
		$res = sql("SELECT * FROM `Bidders` WHERE `ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['ID'];
		$args = ['old_data' => $old_data];
		if(!Bidders_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('Bidders', $selected_id);
}

