		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return Bidders_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if(Request::val('Embedded')) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate)
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return Bidders_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);

		if($AllowDelete)
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$arrPerm['insert']
			&& !$arrPerm['update'] && !$arrPerm['delete'] && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#ContactID').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#ContactID_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#BidNo').replaceWith('<div class=\"form-control-static\" id=\"BidNo\">' + (jQuery('#BidNo').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('input[name=BidderType]').parent().html('<div class=\"form-control-static\">' + jQuery('input[name=BidderType]:checked').next().text() + '</div>')\n";
		$jsReadOnly .= "\tjQuery('input[name=CheckedIn]').parent().html('<div class=\"form-control-static\">' + jQuery('input[name=CheckedIn]:checked').next().text() + '</div>')\n";
		$jsReadOnly .= "\tjQuery('input[name=QuickPay]').parent().html('<div class=\"form-control-static\">' + jQuery('input[name=QuickPay]:checked').next().text() + '</div>')\n";
		$jsReadOnly .= "\tjQuery('#TablePreference').replaceWith('<div class=\"form-control-static\" id=\"TablePreference\">' + (jQuery('#TablePreference').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Card').replaceWith('<div class=\"form-control-static\" id=\"Card\">' + (jQuery('#Card').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(ContactID)%%>', $combo_ContactID->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(ContactID)%%>', $combo_ContactID->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(ContactID)%%>', urlencode($combo_ContactID->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(BidderType)%%>', $combo_BidderType->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(BidderType)%%>', $combo_BidderType->SelectedData, $templateCode);
	$templateCode = str_replace('<%%COMBO(CheckedIn)%%>', $combo_CheckedIn->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(CheckedIn)%%>', $combo_CheckedIn->SelectedData, $templateCode);
	$templateCode = str_replace('<%%COMBO(QuickPay)%%>', $combo_QuickPay->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(QuickPay)%%>', $combo_QuickPay->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['ContactID' => ['Contacts', 'Contact'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(ID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ContactID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(BidNo)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(BidderType)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(CheckedIn)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(QuickPay)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(TotalBids)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(TotalOwed)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(TablePreference)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Card)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(TotalPaid)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ID)%%>', safe_html($urow['ID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ID)%%>', html_attr($row['ID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ID)%%>', urlencode($urow['ID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ContactID)%%>', safe_html($urow['ContactID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ContactID)%%>', html_attr($row['ContactID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactID)%%>', urlencode($urow['ContactID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(BidNo)%%>', safe_html($urow['BidNo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(BidNo)%%>', html_attr($row['BidNo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(BidNo)%%>', urlencode($urow['BidNo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(BidderType)%%>', safe_html($urow['BidderType']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(BidderType)%%>', html_attr($row['BidderType']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(BidderType)%%>', urlencode($urow['BidderType']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(CheckedIn)%%>', safe_html($urow['CheckedIn']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(CheckedIn)%%>', html_attr($row['CheckedIn']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CheckedIn)%%>', urlencode($urow['CheckedIn']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(QuickPay)%%>', safe_html($urow['QuickPay']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(QuickPay)%%>', html_attr($row['QuickPay']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(QuickPay)%%>', urlencode($urow['QuickPay']), $templateCode);
		$templateCode = str_replace('<%%VALUE(TotalBids)%%>', safe_html($urow['TotalBids']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TotalBids)%%>', urlencode($urow['TotalBids']), $templateCode);
		$templateCode = str_replace('<%%VALUE(TotalOwed)%%>', safe_html($urow['TotalOwed']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TotalOwed)%%>', urlencode($urow['TotalOwed']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(TablePreference)%%>', safe_html($urow['TablePreference']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(TablePreference)%%>', html_attr($row['TablePreference']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TablePreference)%%>', urlencode($urow['TablePreference']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Card)%%>', safe_html($urow['Card']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Card)%%>', html_attr($row['Card']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Card)%%>', urlencode($urow['Card']), $templateCode);
		$templateCode = str_replace('<%%VALUE(TotalPaid)%%>', safe_html($urow['TotalPaid']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TotalPaid)%%>', urlencode($urow['TotalPaid']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(ID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ContactID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(BidNo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(BidNo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(BidderType)%%>', 'Bidder', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(BidderType)%%>', urlencode('Bidder'), $templateCode);
		$templateCode = str_replace('<%%VALUE(CheckedIn)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CheckedIn)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(QuickPay)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(QuickPay)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(TotalBids)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TotalBids)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(TotalOwed)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TotalOwed)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(TablePreference)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TablePreference)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Card)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Card)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(TotalPaid)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(TotalPaid)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tContactID_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'Bidders_autofill.php?rnd1=$rnd1&mfk=ContactID&id=' + encodeURIComponent('".addslashes($row['ContactID'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'Bidders_autofill.php?rnd1=$rnd1&mfk=ContactID&id=' + encodeURIComponent(AppGini.current_ContactID{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#ContactID$rnd1').prop('disabled', true); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm['insert'] || (($arrPerm['edit'] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm['edit'] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm['edit'] == 3)) ? "\$j('#ContactID$rnd1').prop('disabled', false); " : "\$j('#ContactID$rnd1').prop('disabled', true); ")." \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#ContactID_caption').length) \$j('#ContactID_caption').click(function() { ContactID_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Bidders');
	if($selected_id) {
		$jdata = get_joined_record('Bidders', $selected_id);
		if($jdata === false) $jdata = get_defaults('Bidders');
		$rdata = $row;
	}
	$templateCode .= loadView('Bidders-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Bidders_dv
	if(function_exists('Bidders_dv')) {
		$args = [];
		Bidders_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}