function Donations_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Donations');
	if(!$arrPerm['insert']) return false;

	$data = [
		'DonationName' => Request::val('DonationName', ''),
		'Description' => Request::val('Description', ''),
		'Restrictions' => Request::val('Restrictions', ''),
		'Value' => Request::val('Value', ''),
		'CatalogID' => Request::lookup('CatalogID', ''),
		'ContactID' => Request::lookup('ContactID', ''),
		'ContactPerson' => Request::val('ContactPerson', ''),
		'ContactPhone' => Request::val('ContactPhone', ''),
		'ItemStatus' => Request::multipleChoice('ItemStatus', ''),
		'ProcuredBy' => Request::val('ProcuredBy', ''),
		'DateProcured' => Request::dateComponents('DateProcured', ''),
		'AdditionalInfo' => Request::multipleChoice('AdditionalInfo', ''),
		'Thanks' => Request::val('Thanks', ''),
		'Notes' => Request::val('Notes', ''),
	];


	// hook: Donations_before_insert
	if(function_exists('Donations_before_insert')) {
		$args = [];
		if(!Donations_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Donations', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Donations', $recID, calculated_fields()['Donations']);

	// hook: Donations_after_insert
	if(function_exists('Donations_after_insert')) {
		$res = sql("SELECT * FROM `Donations` WHERE `ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Donations_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Donations', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Donations_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function Donations_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function Donations_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('Donations', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: Donations_before_delete
	if(function_exists('Donations_before_delete')) {
		$args = [];
		if(!Donations_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `Donations` WHERE `ID`='{$selected_id}'", $eo);

	// hook: Donations_after_delete
	if(function_exists('Donations_after_delete')) {
		$args = [];
		Donations_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='Donations' AND `pkValue`='{$selected_id}'", $eo);
}

function Donations_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('Donations', $selected_id, 'edit')) return false;

	$data = [
		'DonationName' => Request::val('DonationName', ''),
		'Description' => Request::val('Description', ''),
		'Restrictions' => Request::val('Restrictions', ''),
		'Value' => Request::val('Value', ''),
		'CatalogID' => Request::lookup('CatalogID', ''),
		'ContactID' => Request::lookup('ContactID', ''),
		'ContactPerson' => Request::val('ContactPerson', ''),
		'ContactPhone' => Request::val('ContactPhone', ''),
		'ItemStatus' => Request::multipleChoice('ItemStatus', ''),
		'ProcuredBy' => Request::val('ProcuredBy', ''),
		'DateProcured' => Request::dateComponents('DateProcured', ''),
		'AdditionalInfo' => Request::multipleChoice('AdditionalInfo', ''),
		'Thanks' => Request::val('Thanks', ''),
		'Notes' => Request::val('Notes', ''),
	];

	// get existing values
	$old_data = getRecord('Donations', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Donations_before_update
	if(function_exists('Donations_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Donations_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Donations', 
		backtick_keys_once($set), 
		['`ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Donations_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Donations', $data['selectedID'], calculated_fields()['Donations']);

	// hook: Donations_after_update
	if(function_exists('Donations_after_update')) {
		$res = sql("SELECT * FROM `Donations` WHERE `ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['ID'];
		$args = ['old_data' => $old_data];
		if(!Donations_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('Donations', $selected_id);
}

