		$jsReadOnly .= "\tjQuery('#ContactID').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#ContactID_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#ContactPerson').replaceWith('<div class=\"form-control-static\" id=\"ContactPerson\">' + (jQuery('#ContactPerson').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ContactPhone').replaceWith('<div class=\"form-control-static\" id=\"ContactPhone\">' + (jQuery('#ContactPhone').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ItemStatus').replaceWith('<div class=\"form-control-static\" id=\"ItemStatus\">' + (jQuery('#ItemStatus').val() || '') + '</div>'); jQuery('#ItemStatus-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('#s2id_ItemStatus').remove();\n";
		$jsReadOnly .= "\tjQuery('#ProcuredBy').replaceWith('<div class=\"form-control-static\" id=\"ProcuredBy\">' + (jQuery('#ProcuredBy').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#DateProcured').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#DateProcuredDay, #DateProcuredMonth, #DateProcuredYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#AdditionalInfo').replaceWith('<div class=\"form-control-static\" id=\"AdditionalInfo\">' + (jQuery('#AdditionalInfo').val() || '') + '</div>'); jQuery('#AdditionalInfo-multi-selection-help').hide();\n";
