	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(CatalogID)%%>', $combo_CatalogID->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(CatalogID)%%>', $combo_CatalogID->MatchText, $templateCode);
