	$templateCode = str_replace('<%%COMBOTEXT(DateProcured)%%>', $combo_DateProcured->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(AdditionalInfo)%%>', $combo_AdditionalInfo->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(AdditionalInfo)%%>', $combo_AdditionalInfo->SelectedData, $templateCode);
	$templateCode = str_replace('<%%COMBO(Thanks)%%>', $combo_Thanks->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Thanks)%%>', $combo_Thanks->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['CatalogID' => ['Catalog', 'Catalog Number'], 'ContactID' => ['Contacts', 'Donor Name Lookup'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(ID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(DonationName)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Description)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Restrictions)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Value)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(CatalogID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ContactID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ContactPerson)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ContactPhone)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ItemStatus)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ProcuredBy)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(DateProcured)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(AdditionalInfo)%%>', '', $templateCode);
