		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(Restrictions)%%>', '<textarea name="Restrictions" id="Restrictions" rows="5">' . safe_html(htmlspecialchars_decode($row['Restrictions'])) . '</textarea>', $templateCode);
