		$templateCode = str_replace('<%%URLVALUE(Restrictions)%%>', urlencode($urow['Restrictions']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Value)%%>', safe_html($urow['Value']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Value)%%>', html_attr($row['Value']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Value)%%>', urlencode($urow['Value']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(CatalogID)%%>', safe_html($urow['CatalogID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(CatalogID)%%>', html_attr($row['CatalogID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CatalogID)%%>', urlencode($urow['CatalogID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ContactID)%%>', safe_html($urow['ContactID']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ContactID)%%>', html_attr($row['ContactID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactID)%%>', urlencode($urow['ContactID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ContactPerson)%%>', safe_html($urow['ContactPerson']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ContactPerson)%%>', html_attr($row['ContactPerson']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactPerson)%%>', urlencode($urow['ContactPerson']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ContactPhone)%%>', safe_html($urow['ContactPhone']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ContactPhone)%%>', html_attr($row['ContactPhone']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactPhone)%%>', urlencode($urow['ContactPhone']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ItemStatus)%%>', safe_html($urow['ItemStatus']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ItemStatus)%%>', html_attr($row['ItemStatus']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ItemStatus)%%>', urlencode($urow['ItemStatus']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ProcuredBy)%%>', safe_html($urow['ProcuredBy']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ProcuredBy)%%>', html_attr($row['ProcuredBy']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ProcuredBy)%%>', urlencode($urow['ProcuredBy']), $templateCode);
		$templateCode = str_replace('<%%VALUE(DateProcured)%%>', app_datetime($row['DateProcured']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DateProcured)%%>', urlencode(app_datetime($urow['DateProcured'])), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(AdditionalInfo)%%>', safe_html($urow['AdditionalInfo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(AdditionalInfo)%%>', html_attr($row['AdditionalInfo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(AdditionalInfo)%%>', urlencode($urow['AdditionalInfo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Thanks)%%>', safe_html($urow['Thanks']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Thanks)%%>', html_attr($row['Thanks']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Thanks)%%>', urlencode($urow['Thanks']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(Notes)%%>', '<textarea name="Notes" id="Notes" rows="5">' . safe_html(htmlspecialchars_decode($row['Notes'])) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(Notes)%%>', '<div id="Notes" class="form-control-static">' . $row['Notes'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(Notes)%%>', nl2br($row['Notes']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Notes)%%>', urlencode($urow['Notes']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(ID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(DonationName)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DonationName)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(Description)%%>', '<textarea name="Description" id="Description" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(Restrictions)%%>', '<textarea name="Restrictions" id="Restrictions" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(Value)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Value)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(CatalogID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(CatalogID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ContactID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ContactPerson)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactPerson)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ContactPhone)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ContactPhone)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ItemStatus)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ItemStatus)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ProcuredBy)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ProcuredBy)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(DateProcured)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DateProcured)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(AdditionalInfo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(AdditionalInfo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Thanks)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Thanks)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(Notes)%%>', '<textarea name="Notes" id="Notes" rows="5"></textarea>', $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Donations');
	if($selected_id) {
		$jdata = get_joined_record('Donations', $selected_id);
		if($jdata === false) $jdata = get_defaults('Donations');
		$rdata = $row;
	}
	$templateCode .= loadView('Donations-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Donations_dv
	if(function_exists('Donations_dv')) {
		$args = [];
		Donations_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}