function Settings_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Settings');
	if(!$arrPerm['insert']) return false;

	$data = [
		'EventName' => Request::val('EventName', ''),
		'EventDate' => Request::val('EventDate', ''),
		'AnonymousName' => Request::val('AnonymousName', ''),
		'Address' => br2nl(Request::val('Address', '')),
		'RegTicketPrice' => Request::val('RegTicketPrice', ''),
		'DiscTicketPrice' => Request::val('DiscTicketPrice', ''),
		'DinnerCost' => Request::val('DinnerCost', ''),
		'Logo' => Request::fileUpload('Logo', [
			'maxSize' => 512000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => true,
			'dir' => '',
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('Settings', 'Logo', 'tv'));
				createThumbnail($name, getThumbnailSpecs('Settings', 'Logo', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('Settings', 'Logo', Request::val('SelectedID'));
			},
		]),
	];


	// hook: Settings_before_insert
	if(function_exists('Settings_before_insert')) {
		$args = [];
		if(!Settings_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Settings', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Settings', $recID, calculated_fields()['Settings']);

	// hook: Settings_after_insert
	if(function_exists('Settings_after_insert')) {
		$res = sql("SELECT * FROM `Settings` WHERE `ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Settings_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	// record owner is current user
	$recordOwner = getLoggedMemberID();
	set_record_owner('Settings', $recID, $recordOwner);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Settings_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function Settings_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function Settings_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('Settings', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: Settings_before_delete
	if(function_exists('Settings_before_delete')) {
		$args = [];
		if(!Settings_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	// delete file stored in the 'Logo' field
	$res = sql("SELECT `Logo` FROM `Settings` WHERE `ID`='{$selected_id}'", $eo);
	if($row = @db_fetch_row($res)) {
		if($row[0] != '') {
			@unlink(getUploadDir('') . $row[0]);
			$thumbDV = preg_replace('/\.(jpg|jpeg|gif|png|webp)$/i', '_dv.$1', $row[0]);
			$thumbTV = preg_replace('/\.(jpg|jpeg|gif|png|webp)$/i', '_tv.$1', $row[0]);
			@unlink(getUploadDir('') . $thumbTV);
			@unlink(getUploadDir('') . $thumbDV);
		}
	}

	sql("DELETE FROM `Settings` WHERE `ID`='{$selected_id}'", $eo);

	// hook: Settings_after_delete
	if(function_exists('Settings_after_delete')) {
		$args = [];
		Settings_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='Settings' AND `pkValue`='{$selected_id}'", $eo);
}

function Settings_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('Settings', $selected_id, 'edit')) return false;

	$data = [
		'EventName' => Request::val('EventName', ''),
		'EventDate' => Request::val('EventDate', ''),
		'AnonymousName' => Request::val('AnonymousName', ''),
		'Address' => br2nl(Request::val('Address', '')),
		'RegTicketPrice' => Request::val('RegTicketPrice', ''),
		'DiscTicketPrice' => Request::val('DiscTicketPrice', ''),
		'DinnerCost' => Request::val('DinnerCost', ''),
		'Logo' => Request::fileUpload('Logo', [
			'maxSize' => 512000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => true,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('Settings', 'Logo', 'tv'));
				createThumbnail($name, getThumbnailSpecs('Settings', 'Logo', 'dv'));
			},
			'removeOnSuccess' => true,
			'removeOnRequest' => true,
			'remove' => function($selected_id) {
				// delete old file from server
				$oldFile = existing_value('Settings', 'Logo', $selected_id);
				if(!$oldFile) return;

				@unlink(getUploadDir('') . $oldFile);

				// delete thumbnails
				preg_match('/^[a-z0-9_]+\.(jpg|jpeg|gif|png|webp)$/i', $oldFile, $m);
				$thumbDV = str_replace(".{$m[1]}ffffgggg", "_dv.{$m[1]}", $oldFile . 'ffffgggg');
				$thumbTV = str_replace(".{$m[1]}ffffgggg", "_tv.{$m[1]}", $oldFile . 'ffffgggg');
				@unlink(getUploadDir('') . $thumbTV);
				@unlink(getUploadDir('') . $thumbDV);
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('Settings', 'Logo', $selected_id);
			},
		]),
	];

	// get existing values
	$old_data = getRecord('Settings', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: Settings_before_update
	if(function_exists('Settings_before_update')) {
		$args = ['old_data' => $old_data];
		if(!Settings_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'Settings', 
		backtick_keys_once($set), 
		['`ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="Settings_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('Settings', $data['selectedID'], calculated_fields()['Settings']);

	// hook: Settings_after_update
	if(function_exists('Settings_after_update')) {
		$res = sql("SELECT * FROM `Settings` WHERE `ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['ID'];
		$args = ['old_data' => $old_data];
		if(!Settings_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('Settings', $selected_id);
}

