		$templateCode = str_replace('<%%URLVALUE(Address)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(RegTicketPrice)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(RegTicketPrice)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(DiscTicketPrice)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DiscTicketPrice)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(DinnerCost)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DinnerCost)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Logo)%%>', 'blank.gif', $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Settings');
	if($selected_id) {
		$jdata = get_joined_record('Settings', $selected_id);
		if($jdata === false) $jdata = get_defaults('Settings');
		$rdata = $row;
	}
	$templateCode .= loadView('Settings-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Settings_dv
	if(function_exists('Settings_dv')) {
		$args = [];
		Settings_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}